import React from 'react';
import {Route,withRouter,Redirect} from "react-router-dom";
import System from './pages/System';
import Login from './pages/Login';
import './App.css';
import 'antd/dist/antd.css';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state={
      username:"wotuwang",
      password:"123456",
      userValue:false,
      pwdValue:false,
      menu:[
        {
          name:"s"
        }
      ]
    }
    this.login = this.login.bind(this);
    this.goto= this.goto.bind(this);
    this.success = this.success.bind(this);
  }
  componentDidMount(){
    let{history} = this.props;
    // history.push('/home');
    console.log(this.props,history);
  }
  login(){
    let res = this.refs.username.value
    if(res === this.state.username){
     this.setState({
       userValue:true
     })
     console.log("验证成功")
    }else{
      console.log("失败");
    }
  }
  goto(){
    this.props.history.push('/login');
    // let res = this.refs.password.value
    // if(res === this.state.password){
    //  this.setState({
    //    pwdValue:true
    //  })
    //  console.log("验证成功")
    // }else{
    //   console.log("失败");
    // }
  }
  success(){
    console.log(this.state.userValue,this.state.pwdValue)
    // console.log(this.state.menu)
    let{history} = this.props;
    history.push('/home');
    if(this.state.userValue == true && this.state.pwdValue == true){
      alert("登录成功");
    }else{
      console.log("失败");
    }
  }

  render(){

    return (
        <div>
           <switch>
            <Route path="/login" component = {Login} />
            <Route path="/system" component={System} />
            <Redirect from="/" to="/login" exact/>
          </switch>
        </div>     
    )
  }
}
// App = withRouter(App);

export default App;
